mapper.select:
select *
from emp emp left join dept on emp.deptId=dept.deptId


		empId=#{empId}


		and empName like concat('%',#{empName},'%')


		and emp.deptId=#{deptId}


		and dept.deptName=#{deptName}


		and isAdmin=#{isAdmin}


order by empId

mapper.update:
update emp
set avatar=#{avatar}
where empId = #{empId}

update emp
set password=#{newpwd}
where empId = #{empId}
